import axios from "axios";
export default {
  name: "prestataireAddStand",
  data: () => {
    return {
      name: null,
      description: null,
      id_user: null
    };
  },
  methods: {
    goTo(path) {
      this.$router.replace(path);
    },
    add() {
      let error = false;
      if (!this.name) {
        document.querySelector('#name').style.backgroundColor = 'red';
        error = true;
      } else {
        document.querySelector('#name').style.backgroundColor = 'green';
      }
      if (!this.description) {
        document.querySelector('#description').style.backgroundColor = 'red';
        error = true;
      } else {
        document.querySelector('#description').style.backgroundColor = 'green';
      }
      if (!error) {
        axios({
          method: 'post',
          url: 'http://localhost:3000/prestataires/' + this.id_user + '/addStand',
          data: {
            name: this.name,
            description: this.description
          }
        }).then(result => {
          this.$store.commit('setMessage', result.data);
        }).catch(error => {
          console.log(error);
        });
      }
    }
  },
  mounted() {
    this.id_user = this.$store.state.user.id;
  }
};
