import Vue from 'vue'
import Vuex from 'vuex'
import createPersistedState from 'vuex-persistedstate'

Vue.use(Vuex)

export default new Vuex.Store({
  plugins: [createPersistedState({
    storage: window.sessionStorage,
  })],
  state: {
    user: null,
    message:null
  },
  getters: {
  },
  mutations: {
    setUser(state,user){
      state.user = user
    },
    setMessage(state, message){
      state.message = message
      location.reload()
      setTimeout(()=>{state.message.success = -1}, 5000);
    },
    emptyMessage(state){
      state.message = null
    }

  },
  actions: {
  },
  modules: {
  }
})
