import axios from "axios";
export default {
  name: "prestataireShowStands",
  data: () => {
    return {
      id_user: null,
      stands: []
    };
  },
  methods: {
    add() {
      this.$router.replace('/prestataire/stands/add');
    },
    update(id) {
      this.$router.replace('/prestataire/stands/' + id);
    },
    _delete(id) {
      axios({
        method: 'post',
        url: 'http://localhost:3000/prestataires/' + this.id_user + '/stand/' + id + '/delete'
      }).then(result => {
        this.$store.commit('setMessage', result.data);
      }).catch(error => {
        console.log(error);
      });
    },
    signup(id) {
      this.$router.replace('/prestataire/stands/' + id + '/signup');
    }
  },
  mounted() {
    this.id_user = this.$store.state.user.id;
    axios({
      method: 'get',
      url: 'http://localhost:3000/prestataires/' + this.id_user + '/stand'
    }).then(result => {
      this.stands = result.data;
      console.log(result.data());
    }).catch(error => {
      console.log(error);
    });
  }
};
